mapper.select:
select member_time as memberTime
from sys_user
where user_id = #{id}

select count(*)
from t_short_link
where user_id = #{userId}
and group_id in

    #{id}

mapper.delete:
delete
from t_short_link
where user_id = #{userId}
and id in

    #{id}

mapper.update:
update t_short_link
set status = #{status}
where id = #{id}
  and user_id = #{userId}

update t_short_link
set visits_num =#{visitsNum}
where id = #{id}
